mapper.select:
select product.* from product join JJIM on JJIM.JJIM_PRDNUM
= product.PRD_NUM
where JJIM.JJIM_CUSID = #{ID} limit #{page},20;

select count(*) from review 
where 
REV_PRDCODE = #{PRD_CODE} and 
REV_ORDERNUM = #{ORDER_NUM} 
and
 REV_ORDERPRDSIZE = #{ORDER_PRDSIZE};

