mapper.select:

SELECT count(*) FROM PRODUCT
WHERE PRD_PRICE >= #{Minamount}
AND PRD_PRICE <= #{Maxamount}
AND PRD_BRAND = #{condition}
AND PRD_COLOR = #{White}
OR PRD_COLOR = #{Black}
OR PRD_COLOR = #{Blue}
OR PRD_COLOR = #{Gray}
OR PRD_COLOR = #{Green}
OR PRD_COLOR = #{Red}


select *
from PRODUCT
where (PRD_PRICE >= #{minamount} AND PRD_PRICE <= #{maxamount}) 
AND (PRD_GENDER = #{condition} OR PRD_GENDER = #{condition})
OR PRD_BRAND = #{condition}
AND (PRD_COLOR = #{White}
OR PRD_COLOR = #{Black}
OR PRD_COLOR = #{Blue}
OR PRD_COLOR = #{Gray}
OR PRD_COLOR = #{Green}
OR PRD_COLOR = #{Red})
order by PRD_NUM desc limit #{displayPost}, #{postNum}
